mapper.select:
SELECT DECODE(COUNT(1), 0, 'N', 'Y') AS EXISTS_YN
FROM USER_INFO
WHERE USER_ID = #{userId}

SELECT DECODE(COUNT(1), 0, 'N', 'Y') AS EXISTS_YN
FROM USER_INFO
WHERE EMAIL = #{email}

SELECT USER_ID, USER_NAME, EMAIL
FROM USER_INFO
WHERE USER_ID = #{userId}
  AND PASSWORD = #{password}

SELECT
USER_ID,
USER_NAME,
EMAIL
FROM USER_INFO
WHERE 1=1

    AND USER_ID = #{userId}

AND USER_NAME = #{userName}
AND EMAIL = #{email}

mapper.update:
UPDATE USER_INFO
SET PASSWORD = #{password}
WHERE USER_ID = #{userId}
